example.program:
sales = int(input("Enter sales: "))
staff = input("Enter staff type [C]ontract or [F]ulltime: ")

bonus = 0  # assume bonus is 0

if staff == 'F' :
    if sales >= 5000 and sales < 10000 :
        bonus = sales * 0.05
    elif sales >= 10000 :
        bonus = sales * 0.08
    print("The commission amount is $", bonus)
elif staff == 'C' :
    bonus = sales * 0.16
    print("The commission amount is $", bonus)
else :
    print("Wrong Staff Type")

print("The commission amount is $", bonus)
